import axios from 'axios';
import { getToken } from '../util/CookieUtil';

//         'Authorization': getToken() !== null ? 'Bearer ' + getToken() : null,

export default axios.create({
    headers: hasToken(),
    baseURL: 'http://localhost:8081'
});

const hasToken = () => {
    if (getToken()) {
        return {
            common: {
                'Authorization': 'Bearer ' + getToken()
            }
        }
    } else {
        return {}
    }
}
