import React, { useState, useEffect } from 'react'
import { Modal, Input, Button, Header } from 'semantic-ui-react'
import pollingApi from '../apis/pollingApi';
import { checkLogin } from '../util/CookieUtil';
import { HTTP_OK } from '../util/Constants';
import PollHeader from '../components/PollHeader';

const AddQuestion = () => {
    const [pollList, setPollList] = useState([]);

    useEffect(() => {
        checkLogin();
    }, []);

    const [open, setOpen] = React.useState(false);

    return (
        <Modal
            onClose={() => setOpen(false)}
            onOpen={() => setOpen(true)}
            open={open}
            trigger={<Button circular style={{ float: 'right' }} icon='plus circle' />}
        >
            <Modal.Header>Soru Ekle</Modal.Header>
            <Modal.Content image>
                <Modal.Description>
                    <Input fluid placeholder='Soru ekle' />
                    <Header as='h4' textAlign='center'>
                        Cevap Ekle
                    </Header>
                    <Button circular style={{ float: 'right' }} icon='plus circle' />
                </Modal.Description>
            </Modal.Content>
            <Modal.Actions>
                <Button color='black' onClick={() => setOpen(false)}>
                    Vazge√ß
          </Button>
                <Button
                    content="Ekle"
                    labelPosition='right'
                    icon='checkmark'
                    onClick={() => setOpen(false)}
                    positive
                />
            </Modal.Actions>
        </Modal>
    );
}



export default AddQuestion;
