import React from 'react';
import { Container, Grid, Checkbox } from 'semantic-ui-react'
import history from '../history';

const Question = (props) => {
    const renderOptionsList = () => {
        return props.question.optionDtoList.map((option) => {
            return <Answer option={option} />
        });
    }

    return (
        <Container>
            <Grid columns={1} divided>
                <Grid.Row>
                    <Grid.Column>
                        <strong>{`${props.question.index + 1}) `}</strong><label>{props.question.text}</label>
                    </Grid.Column>
                </Grid.Row>

                <Grid.Row >
                    {renderOptionsList()}
                </Grid.Row>
            </Grid>

            <label>{props.text}</label>

        </Container>
    )
}

const Answer = (props) => {
    return (
        <Grid.Column>
            <Checkbox label={{ children: props.option.optionText }} />
        </Grid.Column>
    )
}


export default Question;
