import React, { useState, useEffect } from 'react'
import { Button, List, Form, Grid, Segment } from 'semantic-ui-react'
import pollingApi from '../apis/pollingApi';
import { getToken, getAuthorities } from '../util/CookieUtil';
import history from '../history';
import Header from '../components/Header';
import { HTTP_OK } from '../util/Constants';
import PollItem from '../components/PollItem';

const Home = () => {
    const [pollList, setPollList] = useState([]);

    useEffect(() => {
        if (getToken() === undefined || getToken() === null) {
            history.push('/login');
        }
        fetchPollItems();
    }, []);

    const fetchPollItems = async () => {
        const response = await pollingApi.get('/polls');
        if (response.status === HTTP_OK) {
            setPollList(response.data);
        }
    }

    const renderPollItems = () => {
        return pollList.map(() => {
            <PollItem />
        });

    }

    return (
        <div>
            <Header />
            <List divided relaxed>

            </List>
        </div>
    );
}



export default Home;
