import React, { useState, useEffect } from 'react'
import { Modal, Input, Button, Header, Icon } from 'semantic-ui-react'
import pollingApi from '../apis/pollingApi';
import { checkLogin } from '../util/CookieUtil';
import { HTTP_OK } from '../util/Constants';
import PollHeader from '../components/PollHeader';

const AddQuestion = (props) => {
    const [answerList, setAnswerList] = useState([]);
    const [open, setOpen] = React.useState(false);

    useEffect(() => {
        checkLogin();
    }, []);

    const addQuestion = (e) => {
        let tempAnswerList = answerList;
        tempAnswerList.push({ questionId: props.questionId, optionText: e.target.value });
    }

    const renderQuestionList = () => {
        if (answerList) {
            return answerList.map((option) => {
                return (<div>{option.optionText}<br /></div>);
            });
        }
    }

    return (
        <Modal
            onClose={() => setOpen(false)}
            onOpen={() => setOpen(true)}
            open={open}
            trigger={<Button circular style={{ float: 'right' }} icon='plus circle' />}
        >
            <Modal.Header>Soru Ekle</Modal.Header>
            <Modal.Content image>
                <Modal.Description>
                    <Input fluid placeholder='Soru ekle' />
                    <Header as='h4' textAlign='center'>
                        Seçenekler
                    </Header>

                    <Input
                        fluid
                        icon={<Icon name='plus circle' inverted circular link />}
                        placeholder='Seçenek ekle'
                        onClick={addQuestion}
                    />

                </Modal.Description>
            </Modal.Content>
            <Modal.Actions>
                <Button color='black' onClick={() => setOpen(false)}>
                    Vazgeç
          </Button>
                <Button
                    content="Ekle"
                    labelPosition='right'
                    icon='checkmark'
                    onClick={() => setOpen(false)}
                    positive
                />
            </Modal.Actions>
        </Modal>
    );
}



export default AddQuestion;
