import React, { useState, useEffect } from 'react'
import { Button, Form, Grid, Menu } from 'semantic-ui-react'
import { getToken, getAuthorities } from '../util/CookieUtil';
import history from '../history';
import { END_USER } from '../util/Constants';

const Header = () => {
    const [menuItems, setMenuItems] = useState([]);
    const role = getAuthorities();
    const defaultMenuItems = [
        { key: 'l1', name: 'link-1', content: 'Home' },
        { key: 't1', name: 'text-1', content: 'Logout', position: 'right' },
    ]

    useEffect(() => {
        if (getToken() === undefined || getToken() === null) {
            history.push('/login');
        }
        if (role === END_USER) {
            setMenuItems(defaultMenuItems.splice(1, 0, { key: 'l1', name: 'link-1', content: 'Admin Panel' }));
        } else {
            setMenuItems(defaultMenuItems)
        }
    }, [role]);

    const addAdminLinkToHeader = () => {

    }

    return (
        <div>
            <Grid columns={1} doubling>
                <Grid.Column>
                    <Menu
                        items={menuItems}
                        pointing
                    />
                </Grid.Column>
            </Grid>
        </div>
    );
}



export default Header;
