import React, { useState, useEffect } from 'react'
import { Modal, Input, Button, Header, Icon } from 'semantic-ui-react'
import pollingApi from '../apis/pollingApi';
import { checkLogin } from '../util/CookieUtil';
import { HTTP_OK } from '../util/Constants';
import PollHeader from '../components/PollHeader';

const AddQuestion = (props) => {
    const [answerList, setAnswerList] = useState([]);
    const [optionText, setOptionText] = useState("");
    const [open, setOpen] = React.useState(false);

    useEffect(() => {
        checkLogin();
    }, []);

    const addQuestion = () => {
        if (optionText) {
            let tempAnswerList = answerList;
            tempAnswerList.push({ questionId: props.questionId, optionText: optionText });
            setAnswerList(tempAnswerList);
            setOptionText("");
        }
    }

    const renderQuestionList = () => {
        if (answerList) {
            return answerList.map((option, index) => {
                return (<div><strong>{`${index + 1}) `}</strong>{option.optionText}<br /></div>);
            });
        }
    }

    return (
        <Modal
            onClose={() => setOpen(false)}
            onOpen={() => setOpen(true)}
            open={open}
            trigger={<Button circular style={{ float: 'right' }} icon='plus circle' />}
        >
            <Modal.Header>Soru Ekle</Modal.Header>
            <Modal.Content image>
                <Modal.Description>
                    <Input fluid placeholder='Soru ekle' />
                    <Header as='h4' textAlign='center'>
                        Seçenekler
                    </Header>
                    {renderQuestionList()}
                    <Input
                        fluid
                        value={optionText}
                        icon={<Icon name='plus circle' onClick={addQuestion} inverted circular link />}
                        placeholder='Seçenek ekle'
                        onChange={(e) => { setOptionText(e.target.value) }}
                    />

                </Modal.Description>
            </Modal.Content>
            <Modal.Actions>
                <Button color='black' onClick={() => setOpen(false)}>
                    Vazgeç
          </Button>
                <Button
                    content="Ekle"
                    labelPosition='right'
                    icon='checkmark'
                    onClick={() => setOpen(false)}
                    positive
                />
            </Modal.Actions>
        </Modal>
    );
}

const Answer = () => {

}



export default AddQuestion;
