import React, { useState, useEffect } from 'react'
import { Button, Form, Grid, Menu } from 'semantic-ui-react'
import { getToken, getAuthorities } from '../util/CookieUtil';
import history from '../history';
import { END_USER, ADMIN_USER } from '../util/Constants';

const Header = () => {
    const [menuItems, setMenuItems] = useState([]);

    useEffect(() => {
        if (getToken() === undefined || getToken() === null) {
            history.push('/login');
        }


    }, []);


    return (
        <div>
            <Grid columns={1} doubling>
                <Grid.Column>
                    <Menu>
                        <Menu.Item
                            name='Home'
                            onClick={handleItemClick}
                        />
                        {getAuthorities() === ADMIN_USER ? <Menu.Item
                            name='Admin Panel'
                            onClick={handleItemClick}
                        /> : null}

                        <Menu.Item
                            name='Logout'
                            onClick={handleItemClick}
                        />
                    </Menu>
                </Grid.Column>
            </Grid>
        </div>
    );
}



export default Header;
