import { Container, Grid, Checkbox } from 'semantic-ui-react'
import history from '../history';

const Question = (props) => {
    const [selectedOption, setSelectedOption] = useState();

    const renderOptionsList = () => {
        return props.question.optionDtoList.map((option) => {
            return <Answer option={option} setOption={answer} selectedOption={selectedOption} />
        });
    }

    const answer = (optionNo) => {
        setSelectedOption(optionNo);
    }

    return (
        <Container>
            <Grid columns={1} divided>
                <Grid.Row>
                    <Grid.Column>
                        <strong>{`${props.index + 1}) `}</strong><label>{props.question.text}</label>
                    </Grid.Column>
                </Grid.Row>

                <Grid.Row >
                    {renderOptionsList()}
                </Grid.Row>
            </Grid>

            <label>{props.text}</label>

        </Container>
    )
}

const Answer = (props) => {
    return (
        <Grid.Column>
            <Checkbox label={{ children: props.option.optionText }} checked={props.option.optionNo === props.option.selectedOption} onClick={setOption(props.option.optionNo)} />
        </Grid.Column>
    )
}


export default Question;
