import React, { useState, useEffect } from 'react';
import { Container, Header, Button } from 'semantic-ui-react';
import pollingApi from '../apis/pollingApi';
import { HTTP_OK } from '../util/Constants';
import Question from '../components/Question';
import PollHeader from '../components/PollHeader';
import history from '../history';
import { getUserId } from '../util/CookieUtil'

const Poll = (props) => {
    const [pollQuestions, setPollQuestions] = useState([]);
    let answerMap = new Map();

    useEffect(() => {
        fetchPollQuestions();
    }, [])

    const setAnswer = (questionId, optionId) => {
        answerMap.set(questionId, optionId);
    }

    const handleSubmit = async () => {
        let body = {
            answerMap,
            userId: getUserId()
        }
        const response = await pollingApi.post(`/answers`, body);
        if (response.status === HTTP_OK) {
            //TODO
            //Success Message
        }
        console.log(answerMap);
    }

    const fetchPollQuestions = async () => {
        const response = await pollingApi.get(`/questions/${props.match.params.id}`);
        if (response.status === HTTP_OK) {
            setPollQuestions(response.data);
        }
    }

    const renderQuestionList = () => {
        return pollQuestions.map((question, index) => {
            return <Question question={question} index={index} setAnswer={setAnswer} />
        });
    }

    return (
        <div>
            <PollHeader />
            <Container>
                <Header as='h3' textAlign='center'>
                    {props.location.name}
                </Header>
                {renderQuestionList()}
                <br />
                <div>
                    <Button secondary onClick={handleSubmit}>Vazge√ß</Button>
                    <Button primary onClick={() => history.push('/')}>Kaydet</Button>
                </div>
            </Container>
        </div>

    );
}
export default Poll;
