import React, { useState, useEffect } from 'react';
import { List, Grid, Divider, Header, Icon } from 'semantic-ui-react'
import pollingApi from '../apis/pollingApi';
import PollHeader from '../components/PollHeader';
import { HTTP_OK } from '../util/Constants';

const Statistics = () => {
    const [statisticsList, setStatisticsList] = useState([]);

    useEffect(() => {
        fetchStatistics();
    }, [])

    const fetchStatistics = async () => {
        const response = await pollingApi.get("/admin/answers/statistics");
        if (response.status === HTTP_OK) {
            console.log(response.data);
            setStatisticsList(response.data);
        }
    }

    const renderStatisticItem = () => {
        if (statisticsList) {
            return statisticsList.map((statistic, index) => {
                return <StatisticItem statistic={statistic} index={index} />
            });
        } else {
            return "Hiç istatistik yok";
        }
    }


    /*const renderPendingQuestionItems = () => {
        return pendingQuestionList.map((question, index) => {
            return <Question question={question} index={index} />
        });
    }*/

    return (
        <div>
            <PollHeader />
            <Divider horizontal>
                <Header as='h4'>
                    <Icon name='bar chart' />
                    İstatistik
                 </Header>
            </Divider>
            <List divided relaxed>
                {renderStatisticItem()}
            </List>
        </div>
    )
}

const StatisticItem = (props) => {
    return (
        <div>
            <Grid divided>
                <Grid.Row>
                    <Grid.Column>
                        <label><strong>Soru: </strong> {props.statistic.questionText}</label>
                    </Grid.Column>
                </Grid.Row>

                <div style={{ marginLeft: '3%' }}>
                    <Grid.Row>
                        <Grid.Column>
                            <label><strong>Seçenek: </strong> {props.statistic.statisticOptionDtoList.optionText}</label>
                        </Grid.Column>
                    </Grid.Row>
                    <Grid.Row>
                        <Grid.Column>
                            <label><strong>Seçilme sayısı: </strong> {props.statistic.statisticOptionDtoList.answerCount}</label>
                        </Grid.Column>
                    </Grid.Row>
                </div>
            </Grid>
            <Divider section />
        </div>
    );
}
export default Statistics;
