import React, { useState, useEffect } from 'react';
import { Container, Header } from 'semantic-ui-react';
import pollingApi from '../apis/pollingApi';
import { HTTP_OK } from '../util/Constants';
import Question from '../components/Question';

const Poll = (props) => {
    const [pollQuestions, setPollQuestions] = useState([]);

    useEffect(() => {
        fetchPollQuestions();
    }, [])

    const fetchPollQuestions = async () => {
        const response = await pollingApi.get(`/questions/${props.match.params.id}`);
        if (response.status === HTTP_OK) {
            setPollQuestions(response.data);
        }
    }

    const renderQuestionList = () => {
        return pollQuestions.map((question, index) => {
            return <Question question={question} index={index} />
        });
    }

    return (
        <Container>
            <Header as='h3' textAlign='center'>
                {props.location.name}
            </Header>
            {renderQuestionList()}
        </Container>
    );
}
export default Poll;
