import React, { useState, useEffect } from 'react'
import { Button, Form, Grid, Menu } from 'semantic-ui-react'
import { getToken, getAuthorities } from '../util/CookieUtil';
import history from '../history';

const Header = () => {
    const [userName, setUserName] = useState();
    const [password, setPassword] = useState();

    useEffect(() => {
        if (getToken() === undefined || getToken() === null) {
            history.push('/login');
        }
        console.log(getToken());
        console.log(getAuthorities());
    }, []);

    return (
        <div>
            <Grid columns={1} doubling>

                <Grid.Column>
                    <Menu
                        items={[
                            { key: 'l1', name: 'link-1', content: 'Link' },
                            { key: 'l2', name: 'link-2', content: 'Link' },
                            { key: 't1', name: 'text-1', content: 'Logout', position: 'right' },
                        ]}
                        pointing
                    />
                </Grid.Column>
            </Grid>
        </div>
    );
}



export default Header;
