import React, { useState, useEffect } from 'react'
import { Button, Form, Grid, Menu } from 'semantic-ui-react'
import { getToken, getAuthorities } from '../util/CookieUtil';
import history from '../history';
import { END_USER } from '../util/Constants';
const defaultMenuItems = [
    { key: 'l1', name: 'link-1', content: 'Home' },
    { key: 't1', name: 'text-1', content: 'Logout', position: 'right' },
]


const Header = () => {
    const [menuItems, setMenuItems] = useState(defaultMenuItems);

    useEffect(() => {
        if (getToken() === undefined || getToken() === null) {
            history.push('/login');
        }
        if (getAuthorities() === END_USER) {
            addAdminLinkToHeader();
        }
    }, []);

    const addAdminLinkToHeader = () => {
        setMenuItems(menuItems.splice(1, 0, { key: 'l1', name: 'link-1', content: 'Admin Panel' }));
    }

    return (
        <div>
            <Grid columns={1} doubling>
                <Grid.Column>
                    <Menu
                        items={menuItems}
                        pointing
                    />
                </Grid.Column>
            </Grid>
        </div>
    );
}



export default Header;
