import axios from 'axios';
import { getToken } from '../util/CookieUtil';

//         

export default axios.create({
    headers: {
        common: {
            'Authorization': getToken() !== undefined ? 'Bearer ' + getToken() : ''
        }
    },
    baseURL: 'http://localhost:8081'
});
